#include <iostream>
#include <set>
#include <string>
#include <algorithm>
using namespace std;

// 题意为求最长回文子串， 直接枚举子串首尾位置再判断是否会问，时间复杂度为O(N^3)
// 换个思路，枚举回文串的对称中心位置，向两侧扫描检测最长回文长度时间复杂度为O(N^2)
// 对于最长回文子串问题有对应O(N)算法--Manacher算法

// ======================================暴力算法过不了啊======================================
bool PalindromeStringSize(string s) {
    string str1 = s;
    reverse(str1.begin(),str1.end());
    if (str1.compare(s) == 0) {
        return true;
    } else {
        return false;
    }
}

string longestPalindrome(string s) {
    int size = s.size();
    if (size <= 1) return s;
    int i = 0;
    string max_str = "";
    while (i < size) {
        for (int j = i; j < size; ++j) {
            string str = s.substr(i, j - i + 1);
            cout<<"i "<<i<<"j "<<j<<" "<<str<<endl;
            if (PalindromeStringSize(str) == true) {
                if (str.size() > max_str.size()) {
                    cout<<str<<endl;
                    max_str = str;
                }
            }
        }
        i++;
    }
    return max_str;
}
// ======================================暴力算法过不了啊======================================



int main(int argc, char const *argv[]) {

    string str = "";

    cout<<longestPalindrome(str)<<endl;
    return 0;
}
